int f(int n) {
    vector <pii> div;
    for (int i = 2; i <= sqrt(n); ++i) {
        if (n % i == 0) {
            int cnt = 0;
            while (n % i == 0) {
                n /= i;
                cnt++;
            }
            div.push_back({i,cnt});
        }
    }
    if (div.size() > 1) {
        return -1;
    }
    if (div[0].second % 2 == 1) {
        return -1;
    }
    int div_num = div[0].second;
    for (int i = 2; i <= sqrt(div_num); ++i) {
        if (div_num % i == 0) {
            return -1;
        }
    }
    return div[0].second;
}


bool cmp(pii a, pii b) {
    return a.second > b.second || a.second == b.second &&  a.first > b.first;
}

